    inherit = jmvcore::Analysis,
    public = list(
        initialize = function(options, data=NULL, datasetId="", analysisId="", revision=0) {
            super$initialize(
                package = "snowLatent",
                name = "mlca",
                version = c(1,0,0),
                options = options,
                results = mlcaResults$new(options=options),
                data = data,
