                revision = revision,
                pause = NULL,
                completeWhenFilled = FALSE,
                requiresMissings = FALSE,
                weightsSupport = 'auto')
        }))

#' Multilevel LCA
#'
#' 
#' @param data the data as a data frame
#' @param vars .
#' @param covs .
#' @param group .
#' @param nc .
#' @param nclust .
#' @param fit .
#' @param comp1 .
#' @param rel1 .
#' @param cla .
#' @param cross .
#' @param mpc .
#' @param cpc .
#' @param cn .
#' @param co .
#' @param gof .
#' @param ci .
#' @param item .
#' @param plot1 .
#' @param plot2 .
#' @param angle a number from 0 to 90 defining the angle of the x-axis labels,
#'   where 0 degrees represents completely horizontal labels.
#' @param plot3 .
#' @param width .
#' @param height .
#' @param width1 .
#' @param height1 .
#' @param width2 .
#' @param height2 .
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$instructions} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$text} \tab \tab \tab \tab \tab a preformatted \cr
#'   \code{results$fit} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$comp1} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$rel1} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$cn} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$cla} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$cross} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$mpc} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$cpc} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$gof} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$ci} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$plot1} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$plot2} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$plot3} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$item} \tab \tab \tab \tab \tab an array of tables \cr
#'   \code{results$text3} \tab \tab \tab \tab \tab a preformatted \cr
#' }
#'
#' Tables can be converted to data frames with \code{asDF} or \code{\link{as.data.frame}}. For example:
#'
#' \code{results$fit$asDF}
#'
#' \code{as.data.frame(results$fit)}
#'
#' @export
mlca <- function(
    data,
    vars,
    covs,
    group,
    nc = 2,
    nclust = 2,
    fit = TRUE,
    comp1 = FALSE,
    rel1 = FALSE,
    cla = FALSE,
    cross = FALSE,
    mpc = FALSE,
    cpc = FALSE,
    cn = FALSE,
    co = FALSE,
    gof = FALSE,
    ci = FALSE,
    item = FALSE,
    plot1 = FALSE,
    plot2 = FALSE,
    angle = 0,
    plot3 = FALSE,
    width = 500,
    height = 500,
    width1 = 500,
    height1 = 500,
    width2 = 500,
    height2 = 500) {

    if ( ! requireNamespace("jmvcore", quietly=TRUE))
        stop("mlca requires jmvcore to be installed (restart may be required)")

    if ( ! missing(vars)) vars <- jmvcore::resolveQuo(jmvcore::enquo(vars))
    if ( ! missing(covs)) covs <- jmvcore::resolveQuo(jmvcore::enquo(covs))
    if ( ! missing(group)) group <- jmvcore::resolveQuo(jmvcore::enquo(group))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(vars), vars, NULL),
            `if`( ! missing(covs), covs, NULL),
            `if`( ! missing(group), group, NULL))

    for (v in group) if (v %in% names(data)) data[[v]] <- as.factor(data[[v]])

    options <- mlcaOptions$new(
        vars = vars,
        covs = covs,
        group = group,
        nc = nc,
        nclust = nclust,
        fit = fit,
        comp1 = comp1,
        rel1 = rel1,
        cla = cla,
        cross = cross,
        mpc = mpc,
        cpc = cpc,
        cn = cn,
        co = co,
        gof = gof,
        ci = ci,
        item = item,
        plot1 = plot1,
        plot2 = plot2,
        angle = angle,
        plot3 = plot3,
        width = width,
        height = height,
        width1 = width1,
        height1 = height1,
        width2 = width2,
        height2 = height2)

    analysis <- mlcaClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}

